function loadProfile(a){$("#userInfo").append('<div id="profileBox" class="well"></div>');var b=$("#userInfo").children().last();b.append("<p>First Name: "+a.firstname+"</p>"),b.append("<p>Last Name: "+a.lastname+"</p>"),b.append("<p>Email: "+a.email+"</p>"),b.append("<p>Username: "+a.username+"</p>")}$(document).ready(function(){console.log("Hey it loads!"),$.ajax({type:"GET",url:"/user",success:function(a){console.log(a),$("#welcome").text("Welcome, "+a.username),loadProfile(a)}})});